package com.oops.enc;

public class Encapsulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			Student s1=new Student();
			s1.setId(1);
			s1.setName("Arjun");
			s1.setGrade("A+");
			

				System.out.println(s1);
			
				Student s2=new Student();
				s2.setId(2);
				s2.setName("Vaibhav");
				s2.setGrade("A+");
				
				
				System.out.println(s2);
			
	}

}
